package com.wtf.service.constant;/** * @author wangtengfei * @since 2020/4/26 15:51 */public enum RepairStatusEnum {    create(1,"已创建"),    assign(2,"已分派"),    over(4,"已解决"),    canNotOver(5,"无法解决"),    signIn(3,"已签到"),    leave(6,"已退场");    private Integer status;    private String desc;    public static String getValueByCode(int code) {        for (RepairStatusEnum e : RepairStatusEnum.values()) {            if (e.getStatus() == code) {                return e.getDesc();            }        }        return "";    }    RepairStatusEnum(Integer status, String desc) {        this.status = status;        this.desc = desc;    }    public Integer getStatus() {        return status;    }    public void setStatus(Integer status) {        this.status = status;    }    public String getDesc() {        return desc;    }    public void setDesc(String desc) {        this.desc = desc;    }}