package com.wtf.service.user;import com.wtf.service.constant.ResponseCode;import com.wtf.service.entity.po.UserPO;import com.wtf.service.util.ResponseEntity;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.stereotype.Component;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;/** * @author wangtengfei * @since 2020/3/16 14:38 */@FeignClient(value = "api-user", fallback =UserFallBack.class)public interface IUserFeign {    @RequestMapping(value = "/api/user/getPhone",method = RequestMethod.GET)    ResponseEntity<String> getPhone(@RequestParam(value = "account") String account);    @RequestMapping(value = "/api/user/verifyAcc",method = RequestMethod.GET)    ResponseEntity<UserPO> verifyAcc(@RequestParam(value = "accountOrPhone") String accountOrPhone);}@Componentclass UserFallBack implements IUserFeign {    @Override    public ResponseEntity<String> getPhone(@RequestParam(value = "account") String account) {        return ResponseEntity.failure(ResponseCode.other_api_fail);    }    @Override    public ResponseEntity<UserPO> verifyAcc(@RequestParam(value = "accountOrPhone") String accountOrPhone){        return ResponseEntity.failure(ResponseCode.other_api_fail);    }}