package com.wtf.user.util;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Component;import org.springframework.web.client.RestTemplate;/** * @author wangtengfei * @since 2020/4/10 11:03 */@Componentpublic class HttpSender {    @Autowired    @Qualifier("normalRestTemplate")    private RestTemplate restTemplate;    public String sendGet(String host,String urlParams){        String url=host+urlParams;        return restTemplate.getForObject(url,String.class);    }}